import React from "react";

import { useState } from "react";

const CustomHeader = ({ date, decreaseMonth, increaseMonth, onChangeYear }) => {
  const formattedDate = date.toLocaleString("default", {
    month: "long",
    year: "numeric",
  });

  // const [selectedDate, setSelectedDate] = useState(new Date());

  const handleYearChange = (e) => {
    const selectedYear = parseInt(e.target.value, 10);
    const newDate = new Date(selectedYear, date.getMonth(), date.getDate());
    setSelectedDate(newDate);

    // console.log("Selected Year:", selectedYear);
    onChangeYear(newDate);
  };

  // const handleYearChange = (selectedYear) => {
  // const newDate = new Date(selectedYear, date.getMonth(), date.getDate());
  // setSelectedDate(newDate);
  // };

  // Generate a list of selectable years
  const years = [];
  const currentYear = new Date().getFullYear();
  for (let i = currentYear - 100; i <= currentYear; i++) {
    years.push(i);
  }

  return (
    <div>
      {/* Replace this with your desired styling for the header */}
      <div className="custom-header">
        <div className="button-house">
          <button onClick={decreaseMonth}>{"<"}</button>
          <span>{formattedDate}</span>
          <button onClick={increaseMonth}>{">"}</button>
        </div>
        {/* Dropdown for year selection */}
        <select onChange={handleYearChange} value={date.getFullYear()}>
          {years.map((year) => (
            <option key={year} value={year}>
              {year}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default CustomHeader;
