import React from "react";

export const FormSection1 = ({ formData, onChange }) => {
  const handleLocationChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "location",
        value: e.target.value,
      },
    });
  };

  const handleHearAboutChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "hearAbout",
        value: e.target.value,
      },
    });
  };

  const handleStateChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "state",
        value: e.target.value,
      },
    });
  };

  const handleDateChange = (field, value) => {
    if (field === "month") {
      // Calculate the number of days in the selected month
      const selectedMonth = parseInt(value, 10);
      const selectedYear = parseInt(formData.birthday.year, 10);
      const daysInSelectedMonth = new Date(
        selectedYear,
        selectedMonth,
        0
      ).getDate();

      // Update the formData with the selected month and the correct number of days
      onChange({
        target: {
          name: "birthday",
          value: {
            ...formData.birthday,
            month: value,
            day:
              formData.birthday.day > daysInSelectedMonth
                ? ""
                : formData.birthday.day,
          },
        },
      });
    } else {
      // Update the formData with the selected date field
      onChange({
        target: {
          name: "birthday",
          value: {
            ...formData.birthday,
            [field]: value,
          },
        },
      });
    }
  };

  let state = [
    "AK - Alaska",
    "AL - Alabama",
    "AR - Arkansas",
    "AS - American Samoa",
    "AZ - Arizona",
    "CA - California",
    "CO - Colorado",
    "CT - Connecticut",
    "DC - District of Columbia",
    "DE - Delaware",
    "FL - Florida",
    "GA - Georgia",
    "GU - Guam",
    "HI - Hawaii",
    "IA - Iowa",
    "ID - Idaho",
    "IL - Illinois",
    "IN - Indiana",
    "KS - Kansas",
    "KY - Kentucky",
    "LA - Louisiana",
    "MA - Massachusetts",
    "MD - Maryland",
    "ME - Maine",
    "MI - Michigan",
    "MN - Minnesota",
    "MO - Missouri",
    "MS - Mississippi",
    "MT - Montana",
    "NC - North Carolina",
    "ND - North Dakota",
    "NE - Nebraska",
    "NH - New Hampshire",
    "NJ - New Jersey",
    "NM - New Mexico",
    "NV - Nevada",
    "NY - New York",
    "OH - Ohio",
    "OK - Oklahoma",
    "OR - Oregon",
    "PA - Pennsylvania",
    "PR - Puerto Rico",
    "RI - Rhode Island",
    "SC - South Carolina",
    "SD - South Dakota",
    "TN - Tennessee",
    "TX - Texas",
    "UT - Utah",
    "VA - Virginia",
    "VI - Virgin Islands",
    "VT - Vermont",
    "WA - Washington",
    "WI - Wisconsin",
    "WV - West Virginia",
    "WY - Wyoming",
  ];

  let stateOptions = state.map((stateItem) => (
    <option value={stateItem} key={stateItem}>
      {stateItem}
    </option>
  ));
  console.log(stateOptions);

  return (
    <div className=" FormSection FormSection1">
      <h2 className="form-header">Dealership Information</h2>
      <p>Which location is most convenient for you?</p>
      <label className="birmingham">
        <input
          type="radio"
          name="location"
          value="Birmingham"
          checked={formData.location === "Birmingham"}
          onChange={handleLocationChange}
        />
        {/* Birmingham */}
      </label>
      <label className="diberville">
        <input
          type="radio"
          name="location"
          value="DIberville"
          checked={formData.location === "DIberville"}
          onChange={handleLocationChange}
        />
        {/* D'Iberville */}
      </label>
      <label className="douglasville">
        <input
          type="radio"
          name="location"
          value="Douglasville"
          checked={formData.location === "Douglasville"}
          onChange={handleLocationChange}
        />
        {/* Douglasville */}
      </label>
      <label className="montgomery">
        <input
          type="radio"
          name="location"
          value="Montgomery"
          checked={formData.location === "Montgomery"}
          onChange={handleLocationChange}
        />
        {/* Montgomery */}
      </label>
      <label htmlFor="hearAbout" className="hearAbout">
        <h3>How did you hear about us?</h3>
        <select
          className="dropdown"
          id="hearAbout"
          //   value={agreeValue}
          onChange={handleHearAboutChange}
          //   label="How did you hear about us?"
        >
          <option value="">Select an option</option>
          <option className="custom-dropdown" value="former customer">
            Former Customer
          </option>
          <option className="custom-dropdown" value="auto dealer">
            Auto Dealer
          </option>
          <option className="custom-dropdown" value="customer recommend">
            Customer Recommend
          </option>
          <option className="custom-dropdown" value="website">
            Website
          </option>
          <option className="custom-dropdown" value="search engine">
            Search Engine
          </option>
          <option className="custom-dropdown" value="radio">
            Radio
          </option>
          <option className="custom-dropdown" value="word of mouth">
            Word of Mouth
          </option>
          <option className="custom-dropdown" value="facebook">
            Facebook
          </option>
          <option className="custom-dropdown" value="instagram">
            Instagram
          </option>
          <option className="custom-dropdown" value="youtube">
            Youtube
          </option>
          <option className="custom-dropdown" value="spotify">
            Spotify
          </option>
        </select>
      </label>
      <div className="FormSection1Codes">
        <input
          type="text"
          name="promoCode"
          value={formData.promoCode}
          onChange={onChange}
          placeholder="Promo Code (optional)"
        />

        <input
          type="text"
          name="salesPerson"
          value={formData.salesperson}
          onChange={onChange}
          placeholder="Salesperson spoken with (optional)"
        />
      </div>
      <h2 className="form-header">Personal Information</h2>

      <div className="personalFields">
        <input
          type="text"
          name="firstname"
          value={formData.firstname}
          onChange={onChange}
          placeholder="First Name"
        />
        <input
          type="text"
          name="lastname"
          value={formData.lastname}
          onChange={onChange}
          placeholder="Last Name"
        />
        <input
          type="text"
          name="streetAddress"
          value={formData.streetAddress}
          onChange={onChange}
          placeholder="Street Address"
        />
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={onChange}
          placeholder="City"
        />

        <label htmlFor="state" className="state">
          <select
            className="dropdown"
            id="state"
            value={formData.state}
            onChange={handleStateChange}
          >
            <option value="">State</option>
            {stateOptions}
          </select>
        </label>

        <input
          type="text"
          name="zip"
          value={formData.zip}
          onChange={onChange}
          placeholder="Zip"
        />

        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={onChange}
          placeholder="Email"
        />
        <input
          type="phone"
          name="mobile"
          value={formData.mobile}
          onChange={onChange}
          placeholder="Mobile Number"
        />
        <input
          type="phone"
          name="home"
          value={formData.home}
          onChange={onChange}
          placeholder="Home Number"
        />
      </div>

      <h2 className="form-header">Date of Birth</h2>
      <label htmlFor="birthday" className="birthday">
        <div className="birthday-inputs">
          <select
            className="dropdown"
            id="month"
            value={formData.birthday.month}
            onChange={(e) => handleDateChange("month", e.target.value)}
          >
            {/* Map over the months array to generate options */}
            {months.map((month) => (
              <option key={month.value} value={month.value}>
                {month.label}
              </option>
            ))}
          </select>
          <select
            className="dropdown"
            id="day"
            value={formData.birthday.day}
            onChange={(e) => handleDateChange("day", e.target.value)}
          >
            {/* Map over the daysInMonth array to generate options */}
            {formData.birthday.month &&
              Array.from(
                { length: daysInMonth[parseInt(formData.birthday.month, 10)] },
                (_, i) => i + 1
              ).map((day) => (
                <option key={day} value={day}>
                  {day}
                </option>
              ))}
          </select>
          <input
            type="text"
            name="year"
            value={formData.birthday.year}
            onChange={(e) => handleDateChange("year", e.target.value)}
            placeholder="Year"
          />
        </div>
      </label>
    </div>
  );
};

