import React from "react";
import { Route, Routes, BrowserRouter } from "react-router-dom";

// import "./formInput.css";

import FormSection1 from "./FormSection1";
import FormSection2 from "./FormSection2";

import { useRef, useState } from "react";

// COMPONENTS
// import Login from "./Login";
import SignUp from "./SignUp";
import Dashboard from "./Dashboard";
import FirstLogin from "./FirstLogin";
import FirstLogin2 from "./FirstLogin2";
import FormInput from "./FormInput";
// ANOTHER ONE HERE FOR THE VEHICLES RESULTS PAGE

export default function Form() {
  const [currentStep, setCurrentStep] = useState(1);

  const [formData, setFormData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    username: "",
    password: "",
    passwordconfirm: "",
  });

  const [isFormActive, setIsFormActive] = useState(false);

  const handleSubmit = function (e) {
    e.preventDefault();
    console.log(formData);
    // console.log(e.target.values);
  };

  const handleInputFocus = () => {
    setIsFormActive(true);
  };

  const handleInputBlur = () => {
    setIsFormActive(false);
  };

  // Function to handle "Next" button click
  const handleNext = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  // Function to handle "Previous" button click
  const handlePrevious = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  const onChange = function (e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const inputs = [
    {
      id: 1,
      name: "firstname",
      type: "text",
      placeholder: "First Name",
    },
    {
      id: 2,
      name: "lastname",
      type: "text",
      placeholder: "Last Name",
    },
    {
      id: 3,
      name: "email",
      type: "email",
      placeholder: "Email",
    },
    {
      id: 4,
      name: "phone",
      type: "phone",
      placeholder: "Phone Number",
    },
    {
      id: 5,
      name: "username",
      type: "text",
      placeholder: "Username",
    },
    {
      id: 6,
      name: "password",
      type: "password",
      placeholder: "Password",
    },
    {
      id: 7,
      name: "passwordconfirm",
      type: "password",
      placeholder: "Confirm Password",
    },
  ];

  const totalSteps = 2;

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        {/* {inputs.map((input) => (
          <FormInput
            key={input.id}
            {...input}
            value={values[input.name]}
            onChange={onChange}
            onFocus={handleInputFocus}
            onBlur={handleInputBlur}
          />
        ))} */}
        {/* Render different form sections based on the currentStep */}
        {currentStep === 1 && (
          <FormSection1 formData={formData} onChange={handleChange} />
        )}
        {currentStep === 2 && (
          <FormSection2 formData={formData} onChange={handleChange} />
        )}

        {/* <button className="submit">Submit</button>
        <button className="clear">Clear All</button> */}
        {currentStep > 1 && (
          <button type="button" onClick={handlePrevious}>
            Previous
          </button>
        )}
        {currentStep < totalSteps && (
          <button type="button" onClick={handleNext}>
            Next
          </button>
        )}
        {currentStep === totalSteps && <button type="submit">Submit</button>}
      </form>
    </div>
  );
}
