import React from "react";

export const FormSection1 = ({ formData, onChange }) => {
  const handleLocationChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "location",
        value: e.target.value,
      },
    });
  };

  const handleHearAboutChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "hearAbout",
        value: e.target.value,
      },
    });
  };

  const handleStateChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "state",
        value: e.target.value,
      },
    });
  };

  return (
    <div className=" FormSection FormSection1">
      <h2 className="form-header">Dealership Information</h2>
      <p>Which location is most convenient for you?</p>
      <label className="birmingham">
        <input
          type="radio"
          name="location"
          value="Birmingham"
          checked={formData.location === "Birmingham"}
          onChange={handleLocationChange}
        />
        {/* Birmingham */}
      </label>
      <label className="diberville">
        <input
          type="radio"
          name="location"
          value="DIberville"
          checked={formData.location === "DIberville"}
          onChange={handleLocationChange}
        />
        {/* D'Iberville */}
      </label>
      <label className="douglasville">
        <input
          type="radio"
          name="location"
          value="Douglasville"
          checked={formData.location === "Douglasville"}
          onChange={handleLocationChange}
        />
        {/* Douglasville */}
      </label>
      <label className="montgomery">
        <input
          type="radio"
          name="location"
          value="Montgomery"
          checked={formData.location === "Montgomery"}
          onChange={handleLocationChange}
        />
        {/* Montgomery */}
      </label>
      <label htmlFor="hearAbout" className="hearAbout">
        <h3>How did you hear about us?</h3>
        <select
          className="dropdown"
          id="hearAbout"
          //   value={agreeValue}
          onChange={handleHearAboutChange}
          //   label="How did you hear about us?"
        >
          <option value="">Select an option</option>
          <option className="custom-dropdown" value="former customer">
            Former Customer
          </option>
          <option className="custom-dropdown" value="auto dealer">
            Auto Dealer
          </option>
          <option className="custom-dropdown" value="customer recommend">
            Customer Recommend
          </option>
          <option className="custom-dropdown" value="website">
            Website
          </option>
          <option className="custom-dropdown" value="search engine">
            Search Engine
          </option>
          <option className="custom-dropdown" value="radio">
            Radio
          </option>
          <option className="custom-dropdown" value="word of mouth">
            Word of Mouth
          </option>
          <option className="custom-dropdown" value="facebook">
            Facebook
          </option>
          <option className="custom-dropdown" value="instagram">
            Instagram
          </option>
          <option className="custom-dropdown" value="youtube">
            Youtube
          </option>
          <option className="custom-dropdown" value="spotify">
            Spotify
          </option>
        </select>
      </label>
      <div className="FormSection1Codes">
        <input
          type="text"
          name="promoCode"
          value={formData.promoCode}
          onChange={onChange}
          placeholder="Promo Code (optional)"
        />

        <input
          type="text"
          name="salesPerson"
          value={formData.salesperson}
          onChange={onChange}
          placeholder="Salesperson spoken with (optional)"
        />
      </div>
      <h2 className="form-header">Personal Information</h2>

      <div className="personalFields">
        <input
          type="text"
          name="firstname"
          value={formData.firstname}
          onChange={onChange}
          placeholder="First Name"
        />
        <input
          type="text"
          name="lastname"
          value={formData.lastname}
          onChange={onChange}
          placeholder="Last Name"
        />
        <input
          type="text"
          name="streetAddress"
          value={formData.streetAddress}
          onChange={onChange}
          placeholder="Street Address"
        />
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={onChange}
          placeholder="City"
        />

        <label htmlFor="state" className="state">
          <select
            className="dropdown"
            id="state"
            //   value={agreeValue}
            onChange={handleStateChange}
            //   label="How did you hear about us?"
          >
            <option value="">Select a State</option>
            <option className="custom-dropdown" value="former customer">
              Former Customer
            </option>
            <option className="custom-dropdown" value="auto dealer">
              Auto Dealer
            </option>
            <option className="custom-dropdown" value="customer recommend">
              Customer Recommend
            </option>
            <option className="custom-dropdown" value="website">
              Website
            </option>
            <option className="custom-dropdown" value="search engine">
              Search Engine
            </option>
            <option className="custom-dropdown" value="radio">
              Radio
            </option>
            <option className="custom-dropdown" value="word of mouth">
              Word of Mouth
            </option>
            <option className="custom-dropdown" value="facebook">
              Facebook
            </option>
            <option className="custom-dropdown" value="instagram">
              Instagram
            </option>
            <option className="custom-dropdown" value="youtube">
              Youtube
            </option>
            <option className="custom-dropdown" value="spotify">
              Spotify
            </option>
          </select>
        </label>

        <input
          type="text"
          name="zip"
          value={formData.zip}
          onChange={onChange}
          placeholder="Zip"
        />

        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={onChange}
          placeholder="Email"
        />
        <input
          type="phone"
          name="mobile"
          value={formData.mobile}
          onChange={onChange}
          placeholder="Mobile Number"
        />
        <input
          type="phone"
          name="home"
          value={formData.home}
          onChange={onChange}
          placeholder="Home Number"
        />
      </div>

      {/* date of birth "date" input w/month, day, year */}
    </div>
  );
};

export const FormSection2 = ({ formData, onChange }) => {
  return (
    <div className="FormSection FormSection2">
      {/* Add more inputs for this section */}
    </div>
  );
};

// export default { FormSection1, FormSection2 };
