import React from "react";

export const FormSection1 = ({ formData, onChange }) => {
  const handleLocationChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "location",
        value: e.target.value,
      },
    });
  };

  const generateDayOptions = () => {
    const selectedMonth = formData.birthday.month;
    const selectedYear = formData.birthday.year;

    if (selectedMonth && selectedYear) {
      const daysCount = daysInMonth[selectedMonth].call(
        null,
        parseInt(selectedYear)
      );

      return (
        <>
          <option value="">Day</option>
          {Array.from({ length: daysCount }, (_, i) => i + 1).map((day) => (
            <option key={day} value={day}>
              {day}
            </option>
          ))}
        </>
      );
    } else {
      return <option value="">Day</option>;
    }
  };

  const handleHearAboutChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "hearAbout",
        value: e.target.value,
      },
    });
  };

  const handleStateChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "state",
        value: e.target.value,
      },
    });
  };

  const handleDateChange = (field, value) => {
    if (field === "month") {
      // Update the formData with the selected month
      onChange({
        target: {
          name: "birthday",
          value: {
            ...formData.birthday,
            month: value,
          },
        },
      });
    } else if (field === "year") {
      // Update the formData with the selected year
      onChange({
        target: {
          name: "birthday",
          value: {
            ...formData.birthday,
            year: value,
          },
        },
      });
    } else if (field === "day") {
      // Update the formData with the selected day
      onChange({
        target: {
          name: "birthday",
          value: {
            ...formData.birthday,
            day: value,
          },
        },
      });
    }
  };

  const months = [
    { value: "01", label: "January" },
    { value: "02", label: "February" },
    { value: "03", label: "March" },
    { value: "04", label: "April" },
    { value: "05", label: "May" },
    { value: "06", label: "June" },
    { value: "07", label: "July" },
    { value: "08", label: "August" },
    { value: "09", label: "September" },
    { value: "10", label: "October" },
    { value: "11", label: "November" },
    { value: "12", label: "December" },
  ];

  const daysInMonth = {
    "01": 31,
    "02": (year) => {
      // Check if it's a leap year
      return (year % 4 === 0 && year % 100 !== 0) || year % 400 === 0 ? 29 : 28;
    },
    "03": 31,
    "04": 30,
    "05": 31,
    "06": 30,
    "07": 31,
    "08": 31,
    "09": 30,
    10: 31,
    11: 30,
    12: 31,
  };

  let state = [
    "AK - Alaska",
    "AL - Alabama",
    "AR - Arkansas",
    "AS - American Samoa",
    "AZ - Arizona",
    "CA - California",
    "CO - Colorado",
    "CT - Connecticut",
    "DC - District of Columbia",
    "DE - Delaware",
    "FL - Florida",
    "GA - Georgia",
    "GU - Guam",
    "HI - Hawaii",
    "IA - Iowa",
    "ID - Idaho",
    "IL - Illinois",
    "IN - Indiana",
    "KS - Kansas",
    "KY - Kentucky",
    "LA - Louisiana",
    "MA - Massachusetts",
    "MD - Maryland",
    "ME - Maine",
    "MI - Michigan",
    "MN - Minnesota",
    "MO - Missouri",
    "MS - Mississippi",
    "MT - Montana",
    "NC - North Carolina",
    "ND - North Dakota",
    "NE - Nebraska",
    "NH - New Hampshire",
    "NJ - New Jersey",
    "NM - New Mexico",
    "NV - Nevada",
    "NY - New York",
    "OH - Ohio",
    "OK - Oklahoma",
    "OR - Oregon",
    "PA - Pennsylvania",
    "PR - Puerto Rico",
    "RI - Rhode Island",
    "SC - South Carolina",
    "SD - South Dakota",
    "TN - Tennessee",
    "TX - Texas",
    "UT - Utah",
    "VA - Virginia",
    "VI - Virgin Islands",
    "VT - Vermont",
    "WA - Washington",
    "WI - Wisconsin",
    "WV - West Virginia",
    "WY - Wyoming",
  ];

  let stateOptions = state.map((stateItem) => (
    <option value={stateItem} key={stateItem}>
      {stateItem}
    </option>
  ));
  console.log(stateOptions);

  return (
    <div className=" FormSection FormSection1">
      <h2 className="form-header">Dealership Information</h2>
      <p>Which location is most convenient for you?</p>
      <label className="birmingham">
        <input
