import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import CustomHeader from "./CustomHeader";

const DatePickerWithCustomHeader = () => {
  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const minDate = new Date("01-01-1920");
  const maxDate = new Date();

  return (
    <div>
      <h2 className="form-header birthday-header">Date of Birth</h2>
      <label htmlFor="birthday" className="birthday">
        <div className="birthday-inputs">
          <DatePicker
            selected={selectedDate}
            onChange={handleDateChange}
            monthsShown={1}
            showYearDropdown
            dateFormat="MM/dd/yyyy"
            minDate={minDate}
            maxDate={maxDate}
            renderCustomHeader={({ date, decreaseMonth, increaseMonth }) => (
              <CustomHeader
                selectedDate={date}
                decreaseMonth={decreaseMonth}
                increaseMonth={increaseMonth}
                onChangeYear={handleDateChange}
                minDate={minDate}
                maxDate={maxDate}
              />
            )}
          />
        </div>
      </label>
    </div>
  );
};

export default DatePickerWithCustomHeader;
