import React from "react";

export const FormSection1 = ({ formData, onChange }) => {
  const handleLocationChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "location",
        value: e.target.value,
      },
    });
  };

  return (
    <>
      <h2 className="form-header">Dealership Information</h2>
      <input
        type="check"
        name="location"
        value={formData.location}
        onChange={onChange}
        label="Which location is most convenient for you?"
      />
      <input
        type="text"
        name="lastname"
        value={formData.lastname}
        onChange={onChange}
        placeholder="Last Name"
      />
      {/* Add more inputs for this section */}
    </>
  );
};

export const FormSection2 = ({ formData, onChange }) => {
  return (
    <>
      {/* <h1>Dealership Information</h1> */}

      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={onChange}
        placeholder="Email"
      />
      <input
        type="text"
        name="phone"
        value={formData.phone}
        onChange={onChange}
        placeholder="Phone Number"
      />
      {/* Add more inputs for this section */}
    </>
  );
};

