import React, { useState } from "react";
import DatePicker from "react-datepicker";
// import DatePickerWithCustomHeader from "./DatePickerWithCustomHeader";
import "react-datepicker/dist/react-datepicker.css";
// import CustomHeader from "./CustomHeader";
import "../index.css";
// import { Range, useSlider, getTrackBackground } from "react-range";
import CollapsibleSlider from "./CollapsibleSlider";

let residence = [
  "I own my home",
  "I rent an apartment",
  "I rent a house",
  "I am currently purchasing a home",
  "I live with relatives",
  "Other",
];

let liveWith = ["Relatives", "Friends", "Parents", "Spouse", "Alone"];

let employmentTypes = ["Full Time", "Part Time", "other"];

let state = [
  "AK - Alaska",
  "AL - Alabama",
  "AR - Arkansas",
  "AS - American Samoa",
  "AZ - Arizona",
  "CA - California",
  "CO - Colorado",
  "CT - Connecticut",
  "DC - District of Columbia",
  "DE - Delaware",
  "FL - Florida",
  "GA - Georgia",
  "GU - Guam",
  "HI - Hawaii",
  "IA - Iowa",
  "ID - Idaho",
  "IL - Illinois",
  "IN - Indiana",
  "KS - Kansas",
  "KY - Kentucky",
  "LA - Louisiana",
  "MA - Massachusetts",
  "MD - Maryland",
  "ME - Maine",
  "MI - Michigan",
  "MN - Minnesota",
  "MO - Missouri",
  "MS - Mississippi",
  "MT - Montana",
  "NC - North Carolina",
  "ND - North Dakota",
  "NE - Nebraska",
  "NH - New Hampshire",
  "NJ - New Jersey",
  "NM - New Mexico",
  "NV - Nevada",
  "NY - New York",
  "OH - Ohio",
  "OK - Oklahoma",
  "OR - Oregon",
  "PA - Pennsylvania",
  "PR - Puerto Rico",
  "RI - Rhode Island",
  "SC - South Carolina",
  "SD - South Dakota",
  "TN - Tennessee",
  "TX - Texas",
  "UT - Utah",
  "VA - Virginia",
  "VI - Virgin Islands",
  "VT - Vermont",
  "WA - Washington",
  "WI - Wisconsin",
  "WV - West Virginia",
  "WY - Wyoming",
];

let stateOptions = state.map((stateItem) => (
  <option value={stateItem} key={stateItem}>
    {stateItem}
  </option>
));

// console.log(stateOptions);

let residenceOptions = residence.map((residenceType) => (
  <option value={residenceType} key={residenceType}>
    {residenceType}
  </option>
));

let liveWithOptions = liveWith.map((person) => (
  <option value={person} key={person}>
    {person}
  </option>
));

let employmentTypeOptions = employmentTypes.map((type) => (
  <option value={type} key={type}>
    {type}
  </option>
));

const months = [
  { value: "01", label: "January" },
  { value: "02", label: "February" },
  { value: "03", label: "March" },
  { value: "04", label: "April" },
  { value: "05", label: "May" },
  { value: "06", label: "June" },
  { value: "07", label: "July" },
  { value: "08", label: "August" },
  { value: "09", label: "September" },
  { value: "10", label: "October" },
  { value: "11", label: "November" },
  { value: "12", label: "December" },
];

const justMonthsOptions = [...months.map((month) => month.label)];
// console.log(justMonths);

const daysInMonth = {
  "01": 31,
  "02": (year) => {
    // Check if it's a leap year
    return (year % 4 === 0 && year % 100 !== 0) || year % 400 === 0 ? 29 : 28;
  },
  "03": 31,
  "04": 30,
  "05": 31,
  "06": 30,
  "07": 31,
  "08": 31,
  "09": 30,
  10: 31,
  11: 30,
  12: 31,
};

export const FormSection1 = ({ formData, setFormData, onChange }) => {
  const [selectedDate, setSelectedDate] = useState(null);

  //   const selectedYear = selectedDate ? selectedDate.getFullYear() : "";
  //   console.log(formData.creditScoreEst);

  //   console.log(formData);

  //   const onChangeYear = (year) => {
  //     if (selectedDate) {
  //       // Create a new date object with the selected year and the existing month and day
  //       const updatedDate = new Date(selectedDate);
  //       updatedDate.setFullYear(year);
  //       setSelectedDate(updatedDate);
  //     }
  //   };

  const handleLocationChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "location",
        value: e.target.value,
      },
    });
  };

  const handleHearAboutChange = (e) => {
    console.log(e.target.value);
    // Update the formData with the selected location
    onChange({
      target: {
        name: "hearAbout",
        value: e.target.value,
      },
    });
  };

  const handleStateChange = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "state",
        value: e.target.value,
      },
    });
  };

  const handleLeasedBeforeChange = (e) => {
    // Update the formData with the selected location
    const value = e.target.value;
    onChange({
      target: {
        name: "leasedBefore",
        value: e.target.value,
      },
    });
  };

  const handleLiveInArea = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "liveInArea",
        value: e.target.value,
      },
    });
  };

  const handleCreditScoreEst = (newValues) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      creditScoreEst: newValues[0],
    }));
  };

  const handlePayAwayEnrollChange = (e) => {
    // Update the formData with the checked status of the "Pay-A-Way" checkbox
    onChange({
      target: {
        name: "payAwayEnroll",
        value: e.target.checked,
      },
    });
  };

  //   BIRTHDAY CALENDAR EVENT HANDLER
  const handleDateChange = (date) => {
    setSelectedDate(date);
    if (date) {
      const day = date.getDate();
      const month = date.getMonth() + 1;
      //   console.log(month);
      const year = date.getFullYear();
      onChange({
        target: {
          name: "birthday",
          value: {
            ...formData.birthday,
            day: day,
            month: month,
            year: year,
          },
        },
      });
    } else {
      // Handle the case when the user clears the date
      onChange({
        target: {
          name: "birthday",
          value: {
            day: null,
            month: null,
            year: null,
          },
        },
      });
    }
  };

  // ////////////////////////// RETURNED HTML /////////////////////////////////////////////////

  return (
    <div className=" FormSection FormSection1">
      <h2 className="form-header">Dealership Information</h2>
      <p>Which location is most convenient for you?</p>
      <label className="birmingham">
        <input
          type="radio"
          name="location"
          value="Birmingham"
          checked={formData.location === "Birmingham"}
          onChange={handleLocationChange}
        />
        {/* Birmingham */}
      </label>
      <label className="diberville">
        <input
          type="radio"
          name="location"
          value="DIberville"
          checked={formData.location === "DIberville"}
          onChange={handleLocationChange}
        />
        {/* D'Iberville */}
      </label>
      <label className="douglasville">
        <input
          type="radio"
          name="location"
          value="Douglasville"
          checked={formData.location === "Douglasville"}
          onChange={handleLocationChange}
        />
        {/* Douglasville */}
      </label>
      <label className="montgomery">
        <input
          type="radio"
          name="location"
          value="Montgomery"
          checked={formData.location === "Montgomery"}
          onChange={handleLocationChange}
        />
        {/* Montgomery */}
      </label>
      <label htmlFor="hearAbout" className="hearAbout">
        <h3>How did you hear about us?</h3>
        <select
          className="dropdown"
          id="hearAbout"
          value={formData.hearAbout}
          onChange={handleHearAboutChange}
        >
          <option value="">Select an option</option>
          <option className="custom-dropdown" value="former customer">
            Former Customer
          </option>
          <option className="custom-dropdown" value="auto dealer">
            Auto Dealer
          </option>
          <option className="custom-dropdown" value="customer recommend">
            Customer Recommend
          </option>
          <option className="custom-dropdown" value="website">
            Website
          </option>
          <option className="custom-dropdown" value="search engine">
            Search Engine
          </option>
          <option className="custom-dropdown" value="radio">
            Radio
          </option>
          <option className="custom-dropdown" value="word of mouth">
            Word of Mouth
          </option>
          <option className="custom-dropdown" value="facebook">
            Facebook
          </option>
          <option className="custom-dropdown" value="instagram">
            Instagram
          </option>
          <option className="custom-dropdown" value="youtube">
            Youtube
          </option>
          <option className="custom-dropdown" value="spotify">
            Spotify
          </option>
        </select>
      </label>
      <div className="FormSection1Codes">
        <input
          type="text"
          name="promoCode"
          value={formData.promoCode}
          onChange={onChange}
          placeholder="Promo Code (optional)"
        />

        <input
          type="text"
          name="salesperson"
          value={formData.salesperson}
          onChange={onChange}
          placeholder="Salesperson (optional)"
        />
      </div>

      {/* --------------MIDDLE 'PERSONAL INFORMATION' SECTION OF FORMSECTION1 ------------ */}

      <h2 className="form-header">Personal Information</h2>

      <div className="personalFields">
        <input
          type="text"
          name="firstname"
          value={formData.firstname}
          onChange={onChange}
          placeholder="First Name"
        />
        <input
          type="text"
          name="lastname"
          value={formData.lastname}
          onChange={onChange}
          placeholder="Last Name"
        />
        <input
          type="text"
          name="streetAddress"
          value={formData.streetAddress}
          onChange={onChange}
          placeholder="Street Address"
        />
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={onChange}
          placeholder="City"
        />

        <label htmlFor="state" className="state">
          <select
            className="dropdown"
            id="state"
            value={formData.state}
            onChange={handleStateChange}
          >
            <option value="">State</option>
            {stateOptions}
          </select>
        </label>

        <input
          type="text"
          name="zip"
          value={formData.zip}
          onChange={onChange}
          placeholder="Zip"
        />

        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={onChange}
          placeholder="Email"
        />
        <input
          type="phone"
          name="mobile"
          value={formData.mobile}
          onChange={onChange}
          placeholder="Mobile Number"
        />
        <input
          type="phone"
          name="home"
          value={formData.home}
          onChange={onChange}
          placeholder="Home Number"
        />
      </div>

      {/* --------------LAST SECTION OF FORMSECTION1 ------------ */}

      <div className="personalFields-2">
        <h2 className="form-header SSN-header">Social Security Number</h2>
        <input
          type="text"
          name="ssn"
          value={formData.ssn}
          onChange={onChange}
          placeholder="SSN"
        />

        <h2 className="form-header leasedBefore-header">
          Have you leased with Holmes Motors before?
        </h2>
        <select
          className="dropdown"
          value={formData.leasedBefore}
          name="leasedBefore"
          onChange={handleLeasedBeforeChange}
          placeholder="Have you leased with Holmes before?"
        >
          <option>No</option>
          <option>Yes</option>
        </select>

        <h2 className="form-header liveInArea-header">
          How long have you lived in this area?
        </h2>
        <select
          className="dropdown"
          name="liveInArea"
          value={formData.liveInArea}
          onChange={handleLiveInArea}
        >
          <option>under 1 year</option>
          <option>over 1 year</option>
          <option>over 2 years</option>
          <option>I don't live in this area</option>
        </select>

        <h2 className="form-header birthday-header">Date of Birth</h2>
        <label htmlFor="birthday" className="birthday">
          <div className="datepicker-inputs">
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              maxDate={new Date()}
              minDate={new Date("01-01-1920")}
              monthsShown={1}
              showYearDropdown
              dateFormat="MM/dd/yyyy" // Format the date according to your preference
              className="custom-datepicker"
              //   name = ""
            />
          </div>
        </label>

        <h2 className="form-header creditScoreEst-header">
          What is your estimated Credit Score?
        </h2>
        <p>(Disregard if not doing credit)</p>
        {/* <label>Slider Input:</label> */}

        <CollapsibleSlider
          className="slider"
          min={0}
          max={850}
          step={1}
          values={[formData.creditScoreEst]}
          name="creditScoreEst"
          //   values={formData.creditScoreEst} //number
          onChange={handleCreditScoreEst}
        />
      </div>

