export const FormSection2 = ({ formData, onChange }) => {
  const [selectedDate, setSelectedDate] = useState(null);

  const handleExpChange = (date) => {
    setSelectedDate(date);
    if (date) {
      const day = date.getDate();
      const month = date.getMonth() + 1;
      //   console.log(month);
      const year = date.getFullYear();
      onChange({
        target: {
          name: "driversLicenseExp",
          value: {
            ...formData.driversLicenseExp,
            day: day,
            month: month,
            year: year,
          },
        },
      });
    } else {
      // Handle the case when the user clears the date
      onChange({
        target: {
          name: "driversLicenseExp",
          value: {
            day: null,
            month: null,
            year: null,
          },
        },
      });
    }
  };

  const handleStateChangeDL = (e) => {
    // Update the formData with the selected location
    onChange({
      target: {
        name: "driversLicenseState",
        value: e.target.value,
      },
    });
  };

  return (
    <div className="FormSection FormSection2">
      <h2 className="form-header">Drivers License Information</h2>
      <label>
        <h3>Driver's License Number</h3>
        <input
          type="text"
          name="driversLicenseNum"
          value={formData.handleDriversLicenseNum}
          //   checked={formData.location === "DIberville"}
          onChange={onChange}
          placeholder="Drivers License #"
        />
      </label>

      {/* <h2 className="form-header">Drivers License Information</h2> */}

      <label htmlFor="state" className="state">
        <h3>Driver's License State</h3>
        <select
          className="dropdown"
          id="state"
          value={formData.driversLicenseState}
          onChange={handleStateChangeDL}
        >
          <option value="">State</option>
          {stateOptions}
        </select>
      </label>

      <label htmlFor="birthday" className="birthday">
        <h3>Driver's License Expiration</h3>
        <div className="datepicker-inputs">
          <DatePicker
            selected={selectedDate}
            onChange={handleExpChange}
            maxDate={new Date("01-01-2050")}
            minDate={new Date()}
            monthsShown={1}
            showYearDropdown
            dateFormat="MM/dd/yyyy" // Format the date according to your preference
            className="custom-datepicker"
            //   name = ""
          />
        </div>
        <h3 style={{ marginTop: "2rem" }}>Drivers License Upload</h3>
      </label>
    </div>
  );
};

